package _02控制判断语句;

public class _12九九乘法表 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	        for(int i=1;i<=9;i++) {
	            for(int j=1;j<=i;j++) {
	                System.out.print(i*j + "="+ i + "*" + j + "\t");//用的是System.out.print()不换行
	            }
	          //起到换行的作用
	          System.out.println();
		
			}
		} 
}
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
